import { useContext } from "react";
import { Link } from "react-router-dom";
import { Store } from "../Store";
import {FaPlusCircle, FaMinusCircle} from "react-icons/fa";
 
const CartPage = () => {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  return (
    <div>
      <div className="container mx-auto h-screen">
        <h1 className=" font-thin text-2xl mx-8 py-2">Lista de libros</h1>
        <div className="bg-slate-200 p-1 rounded grid grid-cols-3 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-6 gap-2 mx-8">
          <div className="flex justify-start col-span-3 sm:col-span-3 md:col-span-4 lg:col-span-4 rounded-xl p-3">
            {cartItems.length === 0 ? (
              <div>No hay nada en el carro</div>
            ) : (
              <div>
                { cartItems.map((item) => (
                    <div className="flex justify-start "  key={item.id}>
                      <ul  className="bg-white border border-gray-200 w-96 text-gray-900">
                        <li
                          className="
                                flex justify-start
                                items-center
                                px-2
                                py-2
                                w-full
                                text-gray-400
                                cursor-default
                            "
                        >
                          <img className=" h-[80px]"  src={item.image} alt={item.titulo} />{' '}
                          <Link className="mx-2" to={`/books/${item.codigoISBN}`}>{item.titulo}</Link>
                          <div className=" justify-center ">
                            <button className=" mx-1">
                                <FaPlusCircle></FaPlusCircle>
                            </button>
                                <span>{item.quantity}</span>
                            <button className="mx-1">
                                <FaMinusCircle></FaMinusCircle>
                            </button>

                          </div>
                        </li>
                      </ul>
                    </div>
                ))}
              </div>
            )}
          </div>
          <div className="flex col-span-3 sm:col-span-3 md:col-span-1 lg:col-span-2 items-center  justify-center border-l-2 border-r-2 border-gray-300 rounded-xl p-6 "></div>
        </div>
      </div>
    </div>
  );
};

export default CartPage;
