import { useContext } from "react";
import { Link } from "react-router-dom";
import { Store } from "../Store";

const CartPage = () => {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  return (
    <div>
      <div class="container mx-auto h-screen">
        <h1 className=" font-thin text-2xl mx-8 py-2">Lista de libros</h1>
        <div class="bg-slate-200 p-1 rounded grid grid-cols-3 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-6 gap-2 mx-8">
          <div class="flex justify-center col-span-3 sm:col-span-3 md:col-span-4 lg:col-span-4 rounded-xl p-3">
            {cartItems.length === 0 ? (
              <div>No hay nada en el carro</div>
            ) : (
              <div>
                {cartItems.map((item) => {
                  <div >
                    <div class="flex justify-center" key={item.id}>
                      <ul class="bg-white rounded-lg border border-gray-200 w-96 text-gray-900">
                        <li
                          class="
                                px-6
                                py-2
                                border-b border-gray-200
                                w-full
                                rounded-t-lg
                                text-gray-400
                                cursor-default
                            "
                        >
                          A disabled item
                          <img src={item.image} alt={item.name} />
                          <Link to={`/book/${item.codigoISBN}`}>{item.name}</Link>
                        </li>
                      </ul>
                    </div>
                  </div>
                })}
              </div>
            )}
          </div>
          <div class="flex col-span-3 sm:col-span-3 md:col-span-1 lg:col-span-2 items-center  justify-center border-l-2 border-r-2 border-gray-300 rounded-xl p-6 "></div>
        </div>
      </div>
    </div>
  );
};

export default CartPage;
