import { useState } from "react";
import { useEffect } from "react";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import StepChecks from "../components/StepsChecks";
import { Store } from "../Store";

const PaymentPage = () => {
    const navigate = useNavigate();
    const { state, dispatch: ctxDispatch } = useContext(Store);
    const {
        cart: { shippingAddress, paymentMethod},
    } = state;

    const [paymentMethodName, setPaymentMethod] = useState(
        paymentMethod || 'PayPal'
    );

    useEffect(() => {
        if(!shippingAddress.address) {
            navigate('/shipping')
        }
    },[shippingAddress, navigate])
    const submitHandler = (e) => {
        e.preventDefault();
        ctxDispatch({ type: 'SAVE_PAYMENT_METHOD', payload: paymentMethodName});
        localStorage.setItem('paymentMethod', paymentMethodName);
        navigate('/placeorder')
    };

  return (
    <div className="container  mx-auto md:h-screen md:w-full">
      <StepChecks step1 step2 step3></StepChecks>
      <h1 className=" font-thin text-2xl mx-8 py-2">Metodo de pago</h1>
      <div className="grid justify-items-center items-center py-2 ">
        <div className="block p-6 rounded-lg shadow-lg  bg-white max-w-md container">
        <div class="flex justify-center">
            <div>
                <div class="form-check">
                    <input class="form-check-input appearance-none rounded-full h-4 w-4 border border-gray-300 bg-white checked:bg-blue-600 checked:border-blue-600 focus:outline-none transition duration-200 mt-1 align-top bg-no-repeat bg-center bg-contain float-left mr-2 cursor-pointer" type="radio" name="paypal" id="paypal" value="PayPal" checked={paymentMethodName === 'PayPal'} onChange={(e) => setPaymentMethod(e.target.value)}/>
                    <label class="form-check-label inline-block text-gray-800" >
                        Paypal
                    </label>
                    </div>
                    <div class="form-check">
                    <input class="form-check-input appearance-none rounded-full h-4 w-4 border border-gray-300 bg-white checked:bg-blue-600 checked:border-blue-600 focus:outline-none transition duration-200 mt-1 align-top bg-no-repeat bg-center bg-contain float-left mr-2 cursor-pointer" type="radio" name="stripe" id="stripe" value="PayPal" checked={paymentMethodName === 'Stripe'} onChange={(e) => setPaymentMethod(e.target.value)}/>
                    <label class="form-check-label inline-block text-gray-800" >
                        Stripe
                    </label>
                    </div>
                </div>
                <div className="flex items-center justify-between pb-6">
                    <button
                    type="button"
                    className="inline-block px-6 py-2 border-2  border-red-600 text-red-600 font-medium text-xs leading-tight uppercase rounded hover:bg-black hover:bg-opacity-5 focus:outline-none focus:ring-0 transition duration-150 ease-in-out"
                    data-mdb-ripple="true"
                    data-mdb-ripple-color="light"
                    >
                    Danger
                    </button>
                </div>
            </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
