import { useContext } from "react";
import { Link } from "react-router-dom"
import StepChecks from "../components/StepsChecks"
import { Store } from "../Store";

const OrderPage = () => {
    const { state, dispatch: ctxDispatch } = useContext(Store);
    const { cart, userInfo } = state;
    
    return(
        <div className="container  mx-auto md:h-screen md:w-full">
            <StepChecks step1 step2 step3 step4></StepChecks>
            <h1 className=" font-thin text-2xl mx-8 py-2">Vista de la orden</h1>
            <div class="">
                <div class="block p-3 mx-1 rounded-lg shadow-lg bg-white max-w-sm">
                    <h5 class="text-gray-900 text-xl leading-tight font-medium mb-2">Compra</h5>
                    <p class="text-gray-700 text-base mb-4">
                    Name: {cart.shippingAddress.fullname}
                    </p>
                    <p class="text-gray-700 text-base mb-4">
                    Address: {cart.shippingAddress.address}, {cart.shippingAddress.city},
                    {cart.shippingAddress.postalCode}, {cart.shippingAddress.country}
                    </p>
                </div>
                <Link to='/shipping'>Edit</Link>
            </div>
        </div>
    )
};

export default OrderPage;
