import { useReducer, useEffect, useState } from "react";
import axios from "axios";
import { useParams, Link } from "react-router-dom";
import Rating from "../components/Rating";
import Card from "../components/Card";
import { FaStar, FaHeart, FaEye } from "react-icons/fa";

const reducer = (state, action) => {
  switch (action.type) {
    case "FETCH_REQUEST":
      return { ...state, loading: true };
    case "FETCH_SUCCESS":
      return { ...state, book: action.payload, loading: false };
    case "FETCH_FAIL":
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function BooksInfo() {
  const params = useParams();
  const { codigoISBN } = params;
  // const [books,setBooks] = useState("");

  const [{ loading, error, book }, dispatch] = useReducer(reducer, {
    book: "",
    loading: true,
    error: "",
  });
  useEffect(() => {
    const getData = async () => {
      dispatch({ type: "FETCH_REQUEST" });
      try {
        const result = await axios.get(`/api/books/codigoISBN/${codigoISBN}`);
        dispatch({ type: "FETCH_SUCCESS", payload: result.data });
      } catch (err) {
        dispatch({ type: "FETCH_FAIL", payload: err.message });
      }
    };
    getData();
  }, [codigoISBN]);

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <div>
      <header>Detalle del Producto</header>
      <section>
      <div class="container mx-auto">
  <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
    <div class="flex justify-center text-6xl border-2 border-gray-300 rounded-xl p-6 bg-gray-100">1</div>
    <div class="flex justify-center text-6xl border-2 border-gray-300 rounded-xl p-6 bg-gray-100">2</div>
    <div class="flex justify-center text-6xl border-2 border-gray-300 rounded-xl p-6 bg-gray-100">3</div>
    <div class="flex justify-center text-6xl border-2 border-gray-300 rounded-xl p-6 bg-gray-100">4</div>
  </div>
</div>
      </section>
    </div>
  );
}

export default BooksInfo;
