import { useReducer, useEffect, useState } from 'react';
import axios from 'axios'
import { useParams } from "react-router-dom";
import Rating from "../components/Rating"


const reducer = (state,action) => {
    switch (action.type) {
        case 'FETCH_REQUEST':
        return{...state,loading: true};
        case 'FETCH_SUCCESS':
        return {...state,  book:action.payload, loading:false};
        case 'FETCH_FAIL':
        return {...state, loading:false, error:action.payload};
        default:
        return state;     
    }
};

function BooksInfo () {
    const params = useParams();
    const { codigoISBN } = params;
    // const [books,setBooks] = useState("");


    const [{loading,error,book}, dispatch] = useReducer(reducer,{
        book: "",
        loading: true, 
        error:'',
    });      
    useEffect(() => {
        const getData = async () => {
            dispatch({type:'FETCH_REQUEST'});
            try {
                const result = await axios.get(`/api/books/codigoISBN/${codigoISBN}`);
                dispatch({type: 'FETCH_SUCCESS', payload: result.data});
            } catch (err) {
                dispatch({type: 'FETCH_FAIL', payload:err.message});
            }       
        };
        getData();
    }, [codigoISBN]);
  
    
    return (
        
        loading? <div>Loading...</div>
        : error? <div>{error}</div>
        :
        <div>
            <header>Detalle del Producto</header>
            <section>
                <div className='grid grid-cols-6 gap-4' >
                    <div className='col-start-1 col-span-1 '>
                            <ul className=' '>
                                <li className='py-1'>
                                    <img className='m-full w-32 px-4' src={book.image} alt={book.image} />
                                </li>
                                <li className='py-1'>
                                    <img className='m-full w-32 px-4' src={book.image} alt={book.image} />
                                </li>
                                <li className='py-1'>
                                    <img className='m-full w-32 px-4' src={book.image} alt={book.image} />
                                </li>
                            </ul>  
                  
                        
                      </div>
                      <div className='col-end-5 col-span-3 flex justify-center items-center'>
                        <img className='m-full flex-initial w-96 px-4' src={book.image} alt={book.image} />

                      </div>
                    <div className='font-bold col-end-6 col-span-1 '>
                        <p>{book.titulo}</p>
                        <Rating rating={book.rating} numReviews={book.numReviews}></Rating>
                    </div>
                    
                    <div className='font-bold col-end-7 col-span-1 '>
                        <p>$ {book.precio}</p>
                        Descripcion:
                        <p>{book.descripcion}</p>
                    </div>
                </div>
            </section>
            {book.titulo}
        </div>
    );
};

export default BooksInfo;
